		int $size,
		string $fill = "#000",
		int $weight = 300
	): Dom {
		[ $viewBox, $path ] = static::weight( $weight );

		return (new static)
			->props([ "class" => "Icon IconEngineering" ])
			->add( Svg::create( $size, $fill, $viewBox, $path ));
	}
}
